import React, { useState } from 'react';
import DefaultModal from './DefaultModal';
import MetamaskLoginModal from './MetamaskLoginModal';
import SubscribedModal from './SubscribedModal';
import HistoryInterface from './components/HistoryInterface';
import FooterNav from './components/FooterNav'; // Ensure FooterNav is imported correctly

function App() {
    const [userStatus, setUserStatus] = useState('default');
    const [showHistory, setShowHistory] = useState(false);

    const renderModal = () => {
        // Manage both modal display and history interface
        if (showHistory && (userStatus === 'metamaskLogin' || userStatus === 'subscribed')) {
            return <HistoryInterface />;
        }
        switch (userStatus) {
            case 'metamaskLogin':
                return <MetamaskLoginModal />;
            case 'subscribed':
                return <SubscribedModal />;
            default:
                return <DefaultModal />;
        }
    };

    // Handler to toggle the history display
    const handleHistoryToggle = () => {
        setShowHistory(!showHistory);
    }

    // Update user status and reset history view if changing modes
    const updateStatus = (status) => {
        if (status !== userStatus) {
            setShowHistory(false);
        }
        setUserStatus(status);
    };

    return (
        <div className="app">
            <div className="modal-container">
                {renderModal()}
            </div>
            <FooterNav onNavigate={updateStatus} toggleHistory={handleHistoryToggle} />
        </div>
    );
}

export default App;
