import React, { useState } from 'react';
import DefaultModal from './DefaultModal';
import MetamaskLoginModal from './MetamaskLoginModal';
import SubscribedModal from './SubscribedModal';
import HistoryInterface from './components/HistoryInterface';
import FooterNav from './components/FooterNav';

function App() {
    const [userStatus, setUserStatus] = useState('default');

    const renderModal = () => {
        switch (userStatus) {
            case 'metamaskLogin':
            case 'subscribed':
                return (
                    <>
                        {userStatus === 'metamaskLogin' ? <MetamaskLoginModal /> : <SubscribedModal />}
                        <FooterNav onNavigate={setUserStatus} activeStatus={userStatus} />
                    </>
                );
            case 'history':
                return <HistoryInterface />;
            default:
                return (
                    <>
                        <DefaultModal />
                        <FooterNav onNavigate={setUserStatus} activeStatus={userStatus} />
                    </>
                );
        }
    };

    return (
        <div className="app">
            <div className="modal-container">
                {renderModal()}
            </div>
        </div>
    );
}

export default App;
