import React, { useState } from 'react';
import Header from './components/Header';
import Greeting from './components/Greeting';
import FooterNav from './components/FooterNav';
import './App.css';
import chatIcon from './assets/icons/2.png';

const App = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleChatWindow = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="main-container"> {/* Apply the main container class */}
      <div className="chat-toggle-button" onClick={toggleChatWindow}>
        <img src={chatIcon} alt="Chatbot" className="chat-icon" />
      </div>
      <div className={`chat-window ${isOpen ? 'open' : 'closed'}`}>
        <Header />
        <Greeting />

        <div className="subscription-card">
      <p>Find the right Journaler plan for you and start 30 days free trial for all plans</p>
    </div>

        <FooterNav />
      </div>
    </div>
  );
};

export default App;
