import React, { useState } from 'react';
import DefaultModal from './DefaultModal';
import MetamaskLoginModal from './MetamaskLoginModal';
import SubscribedModal from './SubscribedModal';
import HistoryInterface from './components/HistoryInterface';
import FooterNav from './components/FooterNav';

function App() {
    const [userStatus, setUserStatus] = useState('default');

    const renderChatInterface = () => {
        switch(userStatus) {
            case 'metamaskLogin':
                return <MetamaskLoginModal />;
            case 'subscribed':
                return <SubscribedModal />;
            case 'history':
                return <HistoryInterface />;
            default:
                return <DefaultModal />;
        }
    };

    return (
        <div className="app">
            <div className="chat-window">
                {renderChatInterface()}
                {/* Ensure FooterNav is part of chat window */}
                {['default', 'metamaskLogin', 'subscribed', 'history'].includes(userStatus) && (
                    <FooterNav onNavigate={setUserStatus} />
                )}
            </div>
        </div>
    );
}


export default App;
