import React, { useState } from 'react';
import DefaultModal from './DefaultModal';
import MetamaskLoginModal from './MetamaskLoginModal';
import SubscribedModal from './SubscribedModal';
import HistoryInterface from './components/HistoryInterface';
import FooterNav from './components/FooterNav';

function App() {
    const [userStatus, setUserStatus] = useState('default');

    // Dynamic styling based on the current user status
    const chatWindowStyle = {
        position: 'fixed',
        bottom: '90px',
        right: '20px',
        width: '423px',
        overflow: 'hidden',
        background: 'linear-gradient(to bottom, #0A4777 40%, #FFFFFF 80%)',
        color: '#000000',
        borderRadius: '18px',
        padding: '20px',
        boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
        transition: 'height 0.4s ease, padding 0.3s ease-in-out',
        boxSizing: 'border-box',
        height: userStatus === 'metamaskLogin' || userStatus === 'subscribed' ? '600px' : '500px' // Adjusting height based on modal type
    };

    const renderModal = () => {
        switch (userStatus) {
            case 'metamaskLogin':
                return <MetamaskLoginModal />;
            case 'subscribed':
                return <SubscribedModal />;
            case 'history':
                return <HistoryInterface />;
            default:
                return <DefaultModal />;
        }
    };

    return (
        <div className="app">
            <div style={chatWindowStyle}>
                {renderModal()}
            </div>
            <FooterNav onNavigate={setUserStatus} />
        </div>
    );
}

export default App;
