import React, { useState } from 'react';
import Header from './components/Header';
import Greeting from './components/Greeting';
import Actions from './components/Actions';
import ChatButton from './components/ChatButton';
import FooterNav from './components/FooterNav';
import './App.css';
import minimizeIcon from './assets/icons/minimize-icon.png';


const App = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleChatWindow = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div>
      <div className="chat-toggle-button" onClick={toggleChatWindow}>
        <img src="./assets/robot-icon.png" alt="Chatbot" className="chat-icon" />
      </div>
      <div className={`chat-window ${isOpen ? 'open' : 'closed'}`}>
        <Header />
        <Greeting />
        <Actions />
        <ChatButton />
        <FooterNav />
        <button className="minimize-button" onClick={toggleChatWindow}>
        <img src={minimizeIcon} alt="Minimize" className="minimize-icon.png" />
        </button>
      </div>
    </div>
  );
};

export default App;
