import React, { useState } from 'react';
import Header from './components/Header';
import Greeting from './components/Greeting';
import FooterNav from './components/FooterNav';

import Actions from './components/Actions'; 
import ChatButton from './components/ChatButton'; 
import './MetamaskLoginModal.css'; 
import chatIcon from './assets/icons/Open.svg';

const MetamaskLoginModal = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleChatWindow = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="main-container"> {/* Use the same main container class for consistency */}
      <div className="chat-toggle-button" onClick={toggleChatWindow}>
        <img src={chatIcon} alt="Chatbot" className="chat-icon" />
      </div>
      <div className={`chat-window ${isOpen ? 'open' : 'closed'}`}>
        <Header />
        <Greeting />

        <div className="Metamask-card">
  <div className="subscription-card-title">Sync your Metamask wallet</div>
  <div className="subscription-card-text">
  With just one click, we'll synchronize all your Metamask wallets effortlessly.
  </div>
  </div>

        <Actions label="Link Metamask" onClick={() => console.log('Metamask linked')} />
        <ChatButton />

        <FooterNav />
      </div>
    </div>
  );
};

export default MetamaskLoginModal;
