import React, { useState } from 'react';
import DefaultModal from './DefaultModal';
import MetamaskLoginModal from './MetamaskLoginModal';
import SubscribedModal from './SubscribedModal';
import HistoryInterface from './components/HistoryInterface'; // Adjusted path for consistency
import FooterNav from './components/FooterNav'; // Adjusted path for consistency

function App() {
    const [userStatus, setUserStatus] = useState('default'); // Default view

    const handleNavigation = (view) => {
        setUserStatus(view); // Function to change the user status
    };

    const renderModal = () => {
        switch(userStatus) {
            case 'metamaskLogin':
                return <MetamaskLoginModal />;
            case 'subscribed':
                return <SubscribedModal />;
            case 'history':
                return <HistoryInterface />;
            default:
                return <DefaultModal />;
        }
    };

    return (
        <div className="app">
            {renderModal()}
            <FooterNav onNavigate={handleNavigation} />
        </div>
    );
}

export default App;
